import streamlit as st
from rag_engine import query_groq
import logging
import base64
import os
os.environ["PYTORCH_JIT"] = "0"

# Set up logging
logging.basicConfig(
    level=logging.INFO,
    filename="app.log",
    format="%(asctime)s - %(name)s - %(levelname)s - %(message)s"
)
logger = logging.getLogger(__name__)

# Custom CSS
with open("style.css") as f:
    st.markdown(f"<style>{f.read()}</style>", unsafe_allow_html=True)

# PEC Logo (Placeholder base64; replace with actual logo)
PEC_LOGO_BASE64 = "data:image/png;base64,"  # Placeholder



def main():
    # Header with logo and title
    st.markdown(
        """
        <div class='header'>
            <img src='{}' alt='PEC Logo' class='logo'/>
            <h1>PEC AnswerHub</h1>
            <p>Your AI-Powered Assistant for PEC Engineer Registration Queries</p>
        </div>
        """.format(PEC_LOGO_BASE64),
        unsafe_allow_html=True
    )

    # Hero section
    st.markdown("<div class='hero'>Simplifying PEC Engineers Registration with AI</div>", unsafe_allow_html=True)

    # Query input and submit button
    st.markdown("<div class='query-container'>", unsafe_allow_html=True)
    query = st.text_input(
        "Ask your question about PEC registration:",
        placeholder="E.g., What are the requirements for professional engineer registration?",
        key="query_input"
    )
    submit_button = st.button("Submit", key="submit_button")

    # Handle query submission (button click or Enter key)
    if submit_button or (query and st.session_state.get("query_input") != query):
        if query.strip():
            try:
                with st.spinner("Processing your query..."):
                    logger.info(f"Processing query: {query}")
                    answer = query_groq(query)
                    logger.info(f"Query answered successfully.")
                
                # Display answer in a card
                st.markdown(
                    f"""
                    <div class='answer-card'>
                        <h3>Answer</h3>
                        <p>{answer}</p>
                    </div>
                    """,
                    unsafe_allow_html=True
                )
            except Exception as e:
                logger.error(f"Error processing query: {str(e)}")
                st.error(f"Error processing query: {str(e)}")
        else:
            st.warning("Please enter a valid question.")

    st.markdown("</div>", unsafe_allow_html=True)

    # Feedback section
    st.markdown("<hr>", unsafe_allow_html=True)
    feedback = st.text_area("Provide feedback:", key="feedback")
    if st.button("Submit Feedback"):
        if feedback.strip():
            with open("feedback.txt", "a") as f:
                f.write(f"{feedback}\n")
            st.success("Thank you for your feedback!")
        else:
            st.warning("Please provide feedback before submitting.")

    # Footer with team members
    st.markdown(
        """
        <div class='footer'>
            <hr>
            <p>Developed by: Asif, Yousuf, Zeeshan, Ibrahim, Shafaqat</p>
        </div>
        """,
        unsafe_allow_html=True
    )

if __name__ == "__main__":
    main()